import React, {Component} from 'react';
import expand_more from '../images/expand_more.png';
import expand_less from '../images/expand_less.png';

class Order extends Component {
    state = {...this.props.order, isClosed : true, read: false}

    handleClick= (event) => {
        this.setState({
            isClosed: !this.state.isClosed,
        })
        if(this.state.isClosed){
            event.target.src = expand_more;
        } else event.target.src = expand_less;
    }
    render() { 
        return ( 
            <div className="order" key={this.state.id}>
                <p className="info"><span>NAME:</span>{this.state.name} <img alt="" src={expand_more} onClick={this.handleClick}/></p>
                <p className="action"><span className="action blue-text">{this.state.read ? "Mark Unread" : "Mark read"}</span><span className="action red-text">Delete</span></p>
                <div className={this.state.isClosed ? "hidden" : "shown"}>
                    <p className="info"><span>ADDRESS:</span>{this.state.address.street}</p>
                    <p className="info"><span>SELECT PACK:</span>{this.state.pack}</p>
                    <p className="info"><span>PHONE NUMBER:</span>{this.state.phone}</p>
                    <p className="info"><span>DELIVERY DATE:</span>{this.state.website}</p>
                </div>
                
            </div>
         );
    }
}
 
export default Order;
